import axios from "axios";

const API = "http://localhost:8082/sowings";

export const getAllSowings = () => axios.get(API);
export const registerSowing = (data) => axios.post(API, data);
export const updateSowingStatus = (id, status) =>
  axios.patch(`${API}/${id}/status?status=${encodeURIComponent(status)}`);
export const deleteSowing = (id) => axios.delete(`${API}/${id}`);
