import React, { useState } from "react";
import "../styles/SeedsPage.css";

import RegisterSeed from "../components/RegisterSeed";
import EditSeed from "../components/EditSeed";
import DeleteSeedModal from "../components/DeleteSeedModal";

import searchIcon from "../images/buscar.png";
import filterIcon from "../images/filtrar.png";
import addIcon from "../images/agregar.png";

import seedIconMain from "../images/semilla.png";
import seedIconAlt from "../images/semilla2.png";

import editIcon from "../images/editar.png";
import deleteIcon from "../images/eliminar.png";
import detailsIcon from "../images/detalles.png";

export default function SeedsPage() {
  const [screen, setScreen] = useState("list"); // list | register | edit
  const [showDelete, setShowDelete] = useState(false);

  const items = [
    { id: 1, name: "Tomate chonto", icon: seedIconMain },
    { id: 2, name: "Lechuga crespa", icon: seedIconAlt },
    { id: 3, name: "Brocoli", icon: seedIconMain },
  ];

  if (screen === "register") return <RegisterSeed onBack={() => setScreen("list")} />;
  if (screen === "edit") return <EditSeed onBack={() => setScreen("list")} />;

  return (
    <>
      <div className="seeds-container">
        {/* Toolbar */}
        <div className="toolbar">
          <div className="search-box">
            <img src={searchIcon} alt="" />
            <input type="text" placeholder="Buscar" />
          </div>

          <div className="toolbar-actions">
            <button className="icon-btn">
              <img src={filterIcon} alt="" />
            </button>

            {/* BOTÓN AGREGAR → FORMULARIO REGISTRAR */}
            <button className="icon-btn" onClick={() => setScreen("register")}>
              <img src={addIcon} alt="" />
            </button>
          </div>
        </div>

        {/* Lista */}
        <div className="seeds-list">
          {items.map((item) => (
            <div key={item.id} className="seeds-item">
              <div className="item-info">
                <img src={item.icon} className="item-icon" alt="" />
                <span>{item.name}</span>
              </div>

              <div className="item-actions">
                <button>
                  <img src={detailsIcon} alt="Detalles" />
                </button>

                {/* BOTÓN EDITAR → FORMULARIO EDITAR */}
                <button onClick={() => setScreen("edit")}>
                  <img src={editIcon} alt="Editar" />
                </button>

                {/* BOTÓN ELIMINAR → MODAL */}
                <button onClick={() => setShowDelete(true)}>
                  <img src={deleteIcon} alt="Eliminar" />
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* MODAL DE ELIMINACIÓN */}
      {showDelete && (
        <DeleteSeedModal
          onCancel={() => setShowDelete(false)}
          onConfirm={() => setShowDelete(false)}
        />
      )}
    </>
  );
}
