import axios from "axios";

const API_URL = "http://localhost:8082/sowings";

export const getAllSowings = () => axios.get(API_URL);

export const registerSowing = (data) => axios.post(API_URL, data);

export const deleteSowing = (id) =>
  axios.delete(`${API_URL}/${id}`);


export const updateSowingStatus = (id, status) =>
  axios.patch(`${API_URL}/${id}/status?status=${status}`);
