import React, { useEffect, useState } from 'react';
import '../styles/SowingPage.css';

import {
  getAllSowings,
  updateSowingStatus
} from "../services/sowingService";

import searchIcon from '../images/buscar.png';
import filterIcon from '../images/filtrar.png';
import addIcon from '../images/agregar.png';

import tomatoIcon from '../images/tomate.png';
import lettuceIcon from '../images/lechuga.png';
import broccoliIcon from '../images/brocoli.png';

import editIcon from '../images/editar.png';
import deleteIcon from '../images/eliminar.png';
import detailsIcon from '../images/detalles.png';

export default function SowingPage() {

  const [sowings, setSowings] = useState([]);
  const [search, setSearch] = useState("");

  // ----------------- Cargar desde backend -----------------
  useEffect(() => {
    loadSowings();
  }, []);

  const loadSowings = () => {
    getAllSowings().then(res => setSowings(res.data));
  };

  // ----------------- Buscar -----------------
  const filtered = sowings.filter(item =>
    item.seedName.toLowerCase().includes(search.toLowerCase())
  );

  // ----------------- Asignar icono -----------------
  const getIcon = (seedName) => {
    const name = seedName.toLowerCase();
    if (name.includes("tomate")) return tomatoIcon;
    if (name.includes("lechuga")) return lettuceIcon;
    return broccoliIcon;
  };

  return (
    <div className="sowing-container">

      {/* Barra superior */}
      <div className="toolbar">
        <div className="search-box">
          <img src={searchIcon} alt="" />
          <input
            type="text"
            placeholder="Buscar"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>

        <div className="toolbar-actions">
          <button className="icon-btn"><img src={filterIcon} alt="" /></button>
          <button className="icon-btn"><img src={addIcon} alt="" /></button>
        </div>
      </div>

      {/* Lista */}
      <div className="sowing-list">
        {filtered.map(item => (
          <div key={item.id} className="sowing-item">

            <div className="item-info">
              <img src={getIcon(item.seedName)} className="item-icon" alt="" />
              <span>{item.seedName}</span>
            </div>

            <div className="item-actions">
              <button><img src={detailsIcon} alt="" /></button>
              <button><img src={editIcon} alt="" /></button>
              <button><img src={deleteIcon} alt="" /></button>
            </div>

          </div>
        ))}
      </div>

    </div>
  );
}
