import React, { useEffect, useState } from "react";
import { getSeedById, updateSeed } from "../services/seedService";

export default function EditSeed({ seedId, onClose, onUpdated }) {

  const [form, setForm] = useState(null);

  useEffect(() => {
    getSeedById(seedId).then(res => setForm(res.data));
  }, [seedId]);

  if (!form) return null;

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = () => {
    updateSeed(seedId, form).then(() => {
      onUpdated();
      onClose();
    });
  };

  return (
    <div className="modal">
      <div className="modal-box">
        <h2>Editar Semilla</h2>

        <input name="plantName" value={form.plantName} onChange={handleChange} />
        <input name="variety" value={form.variety} onChange={handleChange} />
        <input name="supplier" value={form.supplier} onChange={handleChange} />
        <input name="quantity" type="number" value={form.quantity} onChange={handleChange} />
        <input name="acquisitionDate" type="date" value={form.acquisitionDate} onChange={handleChange} />
        <input name="expirationDate" type="date" value={form.expirationDate} onChange={handleChange} />

        <button onClick={handleUpdate}>Actualizar</button>
        <button onClick={onClose}>Cancelar</button>
      </div>
    </div>
  );
}
