import React, { useState } from "react";
import { registerSowing } from "../services/sowingService";
import "../styles/SowingModals.css";

export default function RegisterSowing({ onClose, onSaved }) {

  const [form, setForm] = useState({
    seedName: "",
    quantity: "",
    startDate: "",
    harvestDate: "",
    observations: "",
    fumigationFrequency: ""
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    registerSowing({
      ...form,
      quantity: Number(form.quantity),
      fumigationFrequency: Number(form.fumigationFrequency),
      status: "Sembrada"
    }).then(() => {
      onSaved();
      onClose();
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-box sowing-box">

        <h2>Registrar Siembra</h2>

        <input name="seedName" placeholder="Nombre semilla" onChange={handleChange} />
        <input name="quantity" type="number" placeholder="Cantidad" onChange={handleChange} />
        <label>Fecha Inicio</label>
        <input name="startDate" type="date" onChange={handleChange} />
        
        <label>Fecha Cosecha</label>
        <input name="harvestDate" type="date" onChange={handleChange} />

        <textarea
          name="observations"
          placeholder="Observaciones"
          onChange={handleChange}
        />

        <input
          name="fumigationFrequency"
          type="number"
          placeholder="Frecuencia de fumigación (días)"
          onChange={handleChange}
        />

        <button className="btn-submit" onClick={handleSave}>Guardar</button>
        <button className="btn-cancel" onClick={onClose}>Cancelar</button>

      </div>
    </div>
  );
}
