import React, { useEffect, useState } from "react";
import "../styles/SowingPage.css";

import {
  getAllSowings,
  deleteSowing
} from "../services/sowingService";

import searchIcon from "../images/buscar.png";
import filterIcon from "../images/filtrar.png";
import addIcon from "../images/agregar.png";

import tomatoIcon from "../images/tomate.png";
import lettuceIcon from "../images/lechuga.png";
import broccoliIcon from "../images/brocoli.png";

import editIcon from "../images/editar.png";
import deleteIcon from "../images/eliminar.png";
import detailsIcon from "../images/detalles.png";

import RegisterSowing from "../components/sowing/RegisterSowing";
import EditSowing from "../components/sowing/EditSowing";
import DeleteSowingModal from "../components/sowing/DeleteSowingModal";
import ViewSowing from "../components/sowing/ViewSowing";

export default function SowingPage() {
  const [sowings, setSowings] = useState([]);
  const [search, setSearch] = useState("");

  const [openRegister, setOpenRegister] = useState(false);
  const [openEdit, setOpenEdit] = useState(null); // id
  const [openDelete, setOpenDelete] = useState(null); // id
  const [openView, setOpenView] = useState(null); // object

  const icons = {
    tomate: tomatoIcon,
    lechuga: lettuceIcon,
    brocoli: broccoliIcon,
    zucchini: broccoliIcon
  };

  const loadSowings = async () => {
    try {
      const res = await getAllSowings();
      setSowings(res.data);
    } catch (err) {
      console.error("Error loading sowings", err);
    }
  };

  useEffect(() => {
    loadSowings();
  }, []);

  const filtered = sowings.filter(s =>
    s.seedName?.toLowerCase().includes(search.toLowerCase())
  );

  const getIcon = (seedName) => {
    if (!seedName) return tomatoIcon;
    const key = seedName.toLowerCase().split(" ")[0];
    return icons[key] || tomatoIcon;
  };

  return (
    <div className="sowing-container">
      <div className="toolbar">
        <div className="search-box">
          <img src={searchIcon} alt="buscar" />
          <input
            value={search}
            onChange={e => setSearch(e.target.value)}
            placeholder="Buscar"
          />
        </div>

        <div className="toolbar-actions">
          <button className="icon-btn"><img src={filterIcon} alt="filtrar" /></button>
          <button className="icon-btn" onClick={() => setOpenRegister(true)}>
            <img src={addIcon} alt="agregar" />
          </button>
        </div>
      </div>

      <div className="sowing-list">
        {filtered.map(item => (
          <div key={item.id} className="sowing-item">
            <div className="item-info">
              <img src={getIcon(item.seedName)} className="item-icon" alt="" />
              <span>{item.seedName} {item.quantity ? `- ${item.quantity}` : ""}</span>
            </div>

            <div className="item-actions">
              <button onClick={() => setOpenView(item)}><img src={detailsIcon} alt="detalles" /></button>
              <button onClick={() => setOpenEdit(item.id)}><img src={editIcon} alt="editar" /></button>
              <button onClick={() => setOpenDelete(item.id)}><img src={deleteIcon} alt="eliminar" /></button>
            </div>
          </div>
        ))}
      </div>

      {/* Modales */}
      {openRegister && (
        <RegisterSowing
          onClose={() => setOpenRegister(false)}
          onSaved={() => { setOpenRegister(false); loadSowings(); }}
        />
      )}

      {openEdit && (
        <EditSowing
          sowingId={openEdit}
          onClose={() => setOpenEdit(null)}
          onUpdated={() => { setOpenEdit(null); loadSowings(); }}
        />
      )}

      {openDelete && (
        <DeleteSowingModal
          sowingId={openDelete}
          onClose={() => setOpenDelete(null)}
          onDeleted={() => { setOpenDelete(null); loadSowings(); }}
        />
      )}

      {openView && (
        <ViewSowing
          sowing={openView}
          onClose={() => setOpenView(null)}
        />
      )}
    </div>
  );
}
