import React, { useState } from "react";
import { createSeed } from "../services/seedService";
import "../styles/SeedForm.css";
export default function RegisterSeed({ onClose, onSaved }) {

  const [form, setForm] = useState({
    plantName: "",
    variety: "",
    supplier: "",
    quantity: "",
    acquisitionDate: "",
    expirationDate: ""
  });

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    createSeed(form).then(() => {
      onSaved();  
      onClose();  
    });
  };

  return (
    <div className="modal">
      <div className="modal-box">
        <h2>Registrar Semilla</h2>

        <input name="plantName" placeholder="Nombre planta" onChange={handleChange} />
        <input name="variety" placeholder="Variedad" onChange={handleChange} />
        <input name="supplier" placeholder="Proveedor" onChange={handleChange} />
        <input name="quantity" type="number" placeholder="Cantidad" onChange={handleChange} />
        <input name="acquisitionDate" type="date" onChange={handleChange} />
        <input name="expirationDate" type="date" onChange={handleChange} />

        <button onClick={handleSave}>Guardar</button>
        <button onClick={onClose}>Cancelar</button>
      </div>
    </div>
  );
}
